// Import only the specific polyfill needed
const { filter } = require("core-js-pure/stable");

// Add the polyfill to the global object (if not already present)
if (!Array.prototype.filter) {
  Object.defineProperty(Array.prototype, "filter", {
    value: filter,
    writable: true,
    configurable: true,
  });
}

// Export the polyfilled filter method
module.exports = Array.prototype.filter;
