export default function arrayLikeToArray(arr, len) {
  // Use `Math.min` to get the minimum of `len` and `arr.length`
  const actualLen = Math.min(len || arr.length, arr.length);

  // Use `Array.from` to create a new array with the desired length
  // and map function to populate it with the elements from `arr`
  return Array.from({ length: actualLen }, (_, i) => arr[i]);
}
