import type { Config } from '@jest/types';
import type { AllowedConfigOptions } from 'jest-watcher';

type ExtraConfigOptions = Partial<Pick<Config.GlobalConfig, 'noSCM' | 'passWithNoTests'>>;

const defaultConfig: Config.GlobalConfig = {
  // Add any default config values here if needed
};

const mergeConfig = (globalConfig: Config.GlobalConfig, userConfig?: AllowedConfigOptions & ExtraConfigOptions): Config.GlobalConfig => {
  return {
    ...defaultConfig,
    ...globalConfig,
    ...userConfig,
  };
};

export default mergeConfig;
